var body=document.querySelector('body'),menuTrigger=document.querySelector('#toggle-main-menu-mobile'),menuContainer=document.querySelector('#main-menu-mobile');const header=document.querySelector('.header.header-absolute');function toggleModal(){menuContainer.classList.toggle('open'),menuTrigger.classList.toggle('is-active'),body.classList.toggle('lock-scroll')}menuTrigger.onclick=function(){toggleModal(),header.classList.add('header-to-fixed');let a=header.classList.contains('header-to-fixed'),b=document.querySelectorAll('.mobLinks');a&&b.forEach(a=>{a.addEventListener('click',toggleModal)})}